styles:

.ot-bubbletip{
  position: relative;
}
.ot-bubbletip .ot-bubbletip-content {
    min-width: 10em;
    background-color: #FFF;
    color: #fff;
    border-radius: 0px;
    position: absolute;    
    z-index: 4;
}

/* ot-popup arrow */
.ot-bubbletip .ot-bubbletip-content::after {
    content: "";
    z-index: 3;
    display: block;
    position: absolute;
    left: 2em;
    width: 2rem; 
    height: 2rem;
    background: #fff;     
}

.ot-bubbletip .ot-bubbletip-content.bottom{
  top:3rem;
}

.ot-bubbletip .ot-bubbletip-content.bottom:after{
  content: "";
    z-index: 3;
     display: block;
    position: absolute;   
    top:-1rem;    
    background: #fff; 
    -webkit-transform: translateX(-50%) rotate(-134deg);
    transform: translateX(-50%) rotate(-134deg); 
    -webkit-transform: translateX(-50%) rotate(-134deg);
    -moz-box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.698039215686274);
    -webkit-box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.698039215686274);
     box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.698039215686274); 
}

.ot-bubbletip .ot-bubbletip-content.top{
    bottom:3rem;
}

.ot-bubbletip .ot-bubbletip-content.top:after{
    content: "";
    background: #fff; 
    display: block;
    z-index: 3;
    bottom: -1rem;
    -webkit-transform: translateX(-50%) rotate(-134deg);
    transform: translateX(-50%) rotate(-134deg); 
    -moz-box-shadow: -3px -3px 5px rgba(0, 0, 0, 0.698039215686274);
    -webkit-box-shadow: -3px -3px 5px rgba(0, 0, 0, 0.698039215686274);
     box-shadow: -3px -3px 5px rgba(0, 0, 0, 0.698039215686274); 
}

.ot-bubbletip .show {
    visibility: visible;
    display: block !important;
    -moz-box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.698039215686274);
    -webkit-box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.698039215686274);
     box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.698039215686274); 
}
